						var overviewRight = d3.select("#" + this.getId() + "-overview-hidden-right");
						var overviewWindow = d3.select("#" + this.getId() + "-overview-hidden-window");

						var oVisArea = jQuery("#" + this.getId() + "-overview-visArea");
